import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

// Optimized connection pool for performance and memory efficiency
export const pool = new Pool({ 
  connectionString: process.env.DATABASE_URL,
  // Aggressive settings for memory and performance optimization
  max: 8,                  // Reduced max connections to prevent memory overload
  min: 1,                  // Minimum connections  
  idleTimeoutMillis: 10000, // Close idle connections faster (10s)
  connectionTimeoutMillis: 2000, // Faster timeout (2s)
  allowExitOnIdle: true,   // Allow pool to close when idle
});

export const db = drizzle({ client: pool, schema });

// Connection pool monitoring with safe error recovery
let connectionHealth = { errors: 0, lastCheck: Date.now(), backoffTimer: null as NodeJS.Timeout | null };

pool.on('error', (err) => {
  connectionHealth.errors++;
  console.error('ðŸ”¥ Database pool error:', err.message);

  // Implement exponential backoff instead of permanent shutdown
  if (connectionHealth.errors > 5) {
    const backoffMs = Math.min(1000 * Math.pow(2, connectionHealth.errors - 5), 30000); // Max 30s backoff
    console.log(`âš ï¸  High error count (${connectionHealth.errors}), implementing ${backoffMs}ms backoff`);

    // Clear any existing backoff timer
    if (connectionHealth.backoffTimer) {
      clearTimeout(connectionHealth.backoffTimer);
    }

    // Reset error count after successful backoff period
    connectionHealth.backoffTimer = setTimeout(() => {
      console.log('ðŸ”„ Resetting database error count after backoff period');
      connectionHealth.errors = Math.max(0, connectionHealth.errors - 2); // Gradual recovery
      connectionHealth.backoffTimer = null;
    }, backoffMs);
  }

  // Reset error count if errors stop occurring
  if (connectionHealth.errors > 0) {
    setTimeout(() => {
      if (connectionHealth.errors > 0) {
        connectionHealth.errors = Math.max(0, connectionHealth.errors - 1);
      }
    }, 60000); // Reduce error count every minute of stability
  }
});

// Periodic cleanup to prevent memory leaks
setInterval(() => {
  const now = Date.now();
  if (now - connectionHealth.lastCheck > 60000) { // Every minute
    connectionHealth.lastCheck = now;

    // Force garbage collection if available
    if (global.gc) {
      global.gc();
    }
  }
}, 30000);

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import compression from "compression";
import rateLimit from "express-rate-limit";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { 
  setupSecurityMiddleware, 
  applyRouteSpecificSecurity,
  securityErrorHandler,
  requestSizeLimits
} from "./security-middleware";
import { 
  securityMonitoringMiddleware,
  createMonitoringEndpoints 
} from "./monitoring-setup";
import { cache, cacheWarming } from "./caching-strategy";
import { storage } from "./storage";
import { validateProductionConfig, productionConfig } from "./production-config";
import { productionLogger, createRequestLogger, logInfo, logError, logWarn } from "./production-logging";
import { alertManager } from "./production-alerts";

const app = express();

// EMERGENCY: Add compression first for memory optimization
app.use(compression({
  threshold: 1024, // Only compress responses > 1KB
  level: 6, // Moderate compression for balance of speed/size
  filter: (req, res) => {
    // Don't compress if no-transform cache control header is set
    if (req.headers['cache-control'] && req.headers['cache-control'].includes('no-transform')) {
      return false;
    }
    return compression.filter(req, res);
  }
}));

// EMERGENCY: Request throttling for suspicious activity (87 requests from 6 IPs)
const emergencyRateLimit = rateLimit({
  windowMs: 60 * 1000, // 1 minute
  max: 20, // Very restrictive due to suspicious activity
  message: { error: 'Too many requests. Please slow down.' },
  standardHeaders: true,
  legacyHeaders: false,
  skip: (req) => {
    // Skip for authenticated users and health checks
    return req.user || req.path.includes('/health') || req.path.includes('/monitoring');
  }
});

app.use('/api', emergencyRateLimit);

// Setup comprehensive security middleware first
setupSecurityMiddleware(app);

// Add security monitoring
app.use(securityMonitoringMiddleware);

// Body parsing with size limits
app.use(express.json(requestSizeLimits.json));
app.use(express.urlencoded(requestSizeLimits.urlencoded));

// Production request logging middleware with structured logging
app.use(createRequestLogger());

(async () => {
  // Initialize production systems
  logInfo('Production logger initialized', { 
    environment: process.env.NODE_ENV,
    logLevel: process.env.LOG_LEVEL || 'info'
  });

  // Initialize alert manager
  logInfo('Alert manager initialized', {
    alertRules: alertManager.getAlertRules().length,
    environment: process.env.NODE_ENV
  });

  // Validate production configuration before starting
  logInfo('Validating production configuration...', { stage: 'startup' });
  validateProductionConfig();

  const server = await registerRoutes(app);

  // Apply route-specific security after routes are registered
  applyRouteSpecificSecurity(app);

  // Setup monitoring endpoints
  createMonitoringEndpoints(app);

  // Warm caches on startup
  setTimeout(async () => {
    try {
      await productionLogger.measurePerformance('cache-warming-dashboard', 
        () => cacheWarming.warmDashboard(storage));
      await productionLogger.measurePerformance('cache-warming-static', 
        () => cacheWarming.warmStaticData(storage));
      logInfo('Initial cache warming completed', { 
        stage: 'startup',
        caches: ['dashboard', 'static-data']
      });
    } catch (error) {
      logError('Cache warming failed during startup', { 
        stage: 'startup',
        operation: 'cache-warming'
      }, error);
    }
  }, 5000); // Wait 5 seconds after startup

  // EMERGENCY: Global error handler with memory cleanup
  app.use((error: any, req: Request, res: Response, next: NextFunction) => {
    // Force garbage collection on critical errors
    if (global.gc && (error.code === 'ENOMEM' || error.message?.includes('memory'))) {
      console.log('ðŸš¨ Emergency GC triggered by memory error');
      global.gc();
    }

    // Log error with context
    console.error('ðŸ”¥ Server error:', {
      error: error.message,
      stack: error.stack?.split('\n').slice(0, 3).join('\n'),
      url: req.url,
      method: req.method,
      userAgent: req.get('User-Agent'),
      ip: req.ip
    });

    // Use security-aware error handler
    securityErrorHandler(error, req, res, next);
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    logInfo(`Application started successfully`, {
      port,
      host: "0.0.0.0",
      environment: process.env.NODE_ENV || 'development',
      stage: 'startup-complete'
    });
    log(`serving on port ${port}`); // Keep vite logging for development
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import crypto from "crypto";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const isProduction = process.env.NODE_ENV === 'production';

  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
    pruneSessionInterval: 60 * 60 * 1000, // Cleanup expired sessions hourly
  });

  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    rolling: true, // Reset expiration on activity
    name: 'sid', // Don't use default session name
    cookie: {
      httpOnly: true, // Prevent XSS
      secure: isProduction, // HTTPS only in production
      sameSite: isProduction ? 'strict' : 'lax', // CSRF protection
      maxAge: sessionTtl,
    },
    genid: () => {
      // Generate secure session IDs
      return crypto.randomBytes(32).toString('hex');
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    jobTitle: null, // Will be collected during profile completion
    profileImageUrl: claims["profile_image_url"],
    role: "development_officer", // Default role for all new users
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  const domains = process.env.REPLIT_DOMAINS!.split(",");

  // Add localhost support for development
  if (process.env.NODE_ENV !== 'production') {
    domains.push('localhost:5000');
  }

  for (const domain of domains) {
    const isLocalhost = domain.includes('localhost');
    const protocol = isLocalhost ? 'http' : 'https';

    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `${protocol}://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

