  --chart-4: hsl(271 91% 75%); /* Complementary purple */
  --chart-5: hsl(12 76% 71%); /* Complementary orange-red */
  --sidebar: hsl(217 32% 12%); /* Dark school blue sidebar */
  --sidebar-foreground: hsl(0 0% 98%);
