  .shadow-school-lg { box-shadow: var(--shadow-school-lg); }
  .shadow-school-gold { box-shadow: var(--shadow-school-gold); }

  /* Line Clamping */
  .line-clamp-1 {
    display: -webkit-box;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Enhanced focus states for accessibility - WCAG 2.1 AA compliant */
  .focus-ring {
    @apply focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2;
  }

  /* High contrast focus indicator for better accessibility */
  .focus-ring-enhanced {
    @apply focus:outline-none focus:ring-4 focus:ring-blue-600 focus:ring-offset-2 focus:ring-offset-background;
  }

  /* Focus visible only when keyboard navigating */
  .focus-visible-only {
    outline: none;
  }

  .focus-visible-only:focus-visible {
    outline: 3px solid var(--focus-ring-color);
    outline-offset: var(--focus-ring-offset);
  }

  /* Screen reader only content */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }

  /* Show sr-only content when focused */
  .sr-only:focus,
  .sr-only:focus-within {
    position: static;
    width: auto;
    height: auto;
    padding: inherit;
    margin: inherit;
    overflow: visible;
    clip: auto;
    white-space: normal;
  }

  /* Skip link styles */
  .skip-link {
    position: absolute;
    top: -40px;
    left: 6px;
    background: var(--focus-ring-color);
    color: white;
    padding: 8px;
    border-radius: 4px;
    text-decoration: none;
    font-weight: 600;
    z-index: 100;
    transition: top 0.2s ease;
  }

  .skip-link:focus {
    top: 6px;
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    :root {
      --focus-ring-color: hsl(0 0% 0%);
      --border: hsl(0 0% 30%);
    }

    .dark {
      --focus-ring-color: hsl(0 0% 100%);
      --border: hsl(0 0% 70%);
    }
  }

  /* Reduced motion support */
  @media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
    }
  }

  /* School-specific utility classes */
  .school-primary {
    @apply bg-primary text-primary-foreground;
