    @apply bg-gradient-to-r from-primary via-primary/95 to-primary/90;
  }

  /* Animation utilities */
  .animate-slide-up {
    animation: slideUp 0.7s ease-out forwards;
  }

  .animate-scale-in {
    animation: scaleIn 0.3s ease-out forwards;
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-out forwards;
  }

  @keyframes slideUp {
