import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/lib/auth-context";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger 
} from "@/components/ui/dropdown-menu";
import { MobileDrawerTrigger } from "@/components/layout/mobile-drawer";
import { isMobileDevice } from "@/lib/navigation-utils";
import { cn } from "@/lib/utils";
import logoUrl from "@assets/image_1758026275177.png";

export function Header() {
  const [location] = useLocation();
  const { user, getUserDisplayName, getRoleDisplayName, logout } = useAuth();

  // Simple page titles without complexity
  const getPageTitle = () => {
    switch (location) {
      case "/": return "Dashboard";
      case "/donors": return "Donors";
      case "/campaigns": return "Campaigns";
      case "/communications": return "Communications";
      case "/analytics": return "Analytics";
      case "/segments": return "Segments";
      case "/import": return "Import Data";
      case "/settings": return "Settings";
      default: return "Dashboard";
    }
  };

  const getPageDescription = () => {
    switch (location) {
      case "/": return "Your fundraising overview and key metrics";
      case "/donors": return "Manage donor profiles and relationships";
      case "/campaigns": return "Create and track fundraising campaigns";
      case "/communications": return "Email communications and templates";
      case "/analytics": return "Performance insights and reports";
      case "/segments": return "Organize donors into targeted groups";
      case "/import": return "Import donor data from files";
      case "/settings": return "Account and system settings";
      default: return "School in the Square Fundraising Platform";
    }
  };

  return (
    <header className="bg-white border-b border-school-blue-200 shadow-school sticky top-0 z-20">
      <div className="px-4 sm:px-6 py-4 sm:py-5">
        <div className="flex items-center justify-between">
          {/* Left Section */}
          <div className="flex items-center gap-4 sm:gap-6 min-w-0 flex-1">
            {/* Mobile Menu Trigger */}
            <div className="lg:hidden">
              <MobileDrawerTrigger />
            </div>

            {/* School Logo and Branding - Prominently Displayed */}
            <div className="flex items-center gap-3 sm:gap-4 hidden lg:flex">
              <div className="w-16 h-16 xl:w-20 xl:h-20 flex items-center justify-center bg-white rounded-xl shadow-school border border-school-blue-100">
                <img 
                  src={logoUrl} 
                  alt="School in the Square Logo" 
                  className="w-full h-full object-contain p-1"
                />
              </div>
              <div className="flex flex-col">
                <h2 className="text-school-subheading font-bold text-school-blue-900 leading-tight">
                  School in the Square
                </h2>
                <p className="text-school-small text-school-blue-600 font-medium">
                  Fundraising Platform
                </p>
              </div>
            </div>

            {/* Page Title & Description */}
            <div className="min-w-0 flex-1">
              <h1 className="text-school-heading text-school-blue-900 font-bold tracking-tight truncate">
                {getPageTitle()}
              </h1>

              <p className="text-school-body text-school-blue-600 leading-tight hidden sm:block">
                {getPageDescription()}
              </p>
            </div>
          </div>

          {/* Right Section - User Profile Only */}
          <div className="flex items-center">
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button 
                  variant="ghost" 
                  className="flex items-center gap-2 lg:gap-3 px-2 lg:px-4 py-2 lg:py-3 h-auto rounded-lg hover:bg-school-blue-50 hover:border-school-blue-200 border border-transparent transition-all duration-200" 
                  data-testid="button-user-menu"
                >
                  <Avatar className="h-8 w-8 lg:h-9 lg:w-9 ring-2 ring-school-blue-100">
                    <AvatarImage src={user?.profileImageUrl} alt={getUserDisplayName()} />
                    <AvatarFallback className="bg-school-blue-500 text-white font-semibold text-sm" data-testid="text-user-initials">
                      {getUserDisplayName().split(' ').map(n => n[0]).join('').toUpperCase() || 'U'}
                    </AvatarFallback>
                  </Avatar>
                  <div className="text-left hidden lg:block">
                    <div className="text-sm lg:text-base font-semibold text-school-blue-900 truncate max-w-32" data-testid="text-user-name">
                      {getUserDisplayName() || 'User'}
                    </div>
                    <div className="text-xs lg:text-sm text-school-blue-600 truncate max-w-32" data-testid="text-user-role">
                      {user?.jobTitle || getRoleDisplayName()}
                    </div>
                  </div>
                  <i className="fas fa-chevron-down text-xs text-school-blue-400 hidden lg:block" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end" className="w-56 lg:w-64 border-school-blue-200 shadow-school-lg" data-testid="menu-user-dropdown">
                <DropdownMenuLabel className="text-sm lg:text-base font-semibold text-school-blue-900">
                  My Account
                </DropdownMenuLabel>
                <DropdownMenuSeparator className="bg-school-blue-100" />

                <DropdownMenuItem className="text-school-blue-700 hover:bg-school-blue-50 hover:text-school-blue-900 cursor-pointer" data-testid="menuitem-profile">
                  <i className="fas fa-user mr-3 text-school-blue-500 w-4" />
                  Profile Settings
                </DropdownMenuItem>
                <DropdownMenuItem className="text-school-blue-700 hover:bg-school-blue-50 hover:text-school-blue-900 cursor-pointer" data-testid="menuitem-preferences">
                  <i className="fas fa-cog mr-3 text-school-blue-500 w-4" />
                  Preferences
                </DropdownMenuItem>

                <DropdownMenuSeparator className="bg-school-blue-100" />
                <DropdownMenuItem onClick={logout} className="text-red-600 hover:bg-red-50 hover:text-red-700 cursor-pointer" data-testid="menuitem-logout">
                  <i className="fas fa-sign-out-alt mr-3 text-red-500 w-4" />
                  Sign Out
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>

        {/* Mobile Logo and Description */}
        <div className="lg:hidden mt-3 flex items-center gap-3">
          <div className="w-12 h-12 flex items-center justify-center bg-white rounded-lg shadow-school border border-school-blue-100">
            <img 
              src={logoUrl} 
              alt="School in the Square Logo" 
              className="w-full h-full object-contain p-1"
            />
          </div>
          <div className="flex flex-col flex-1">
            <h2 className="text-school-body font-bold text-school-blue-900">
              School in the Square
            </h2>
            <p className="text-school-small text-school-blue-600 hidden sm:block">
              {getPageDescription()}
            </p>
          </div>
        </div>
      </div>
    </header>
  );
}

// Simple hook for header state management without complexity  
export function useHeaderContext() {
  const [location] = useLocation();

  return {
    currentPage: location
  };
}

client/src/components/layout/sidebar.tsx.content:
import { Link, useLocation } from "wouter";
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import { useAuth } from "@/lib/auth-context";
import { isDesktopDevice } from "@/lib/navigation-utils";
import { PERMISSIONS } from "@shared/permissions";
import logoUrl from "@assets/image_1758026275177.png";

// Simplified navigation configuration
const NAVIGATION_ITEMS = [
  {
    id: 'dashboard',
    label: 'Dashboard',
    icon: 'fas fa-tachometer-alt',
    path: '/',
    description: 'Your fundraising overview'
  },
  {
    id: 'donors',
    label: 'Donors',
    icon: 'fas fa-users',
    path: '/donors',
    permissions: [PERMISSIONS.DONORS_VIEW],
    description: 'Manage donor relationships'
  },
  {
    id: 'campaigns',
    label: 'Campaigns',
    icon: 'fas fa-bullhorn',
    path: '/campaigns',
    permissions: [PERMISSIONS.CAMPAIGNS_VIEW],
    description: 'Create and track campaigns'
  },
  {
    id: 'communications',
    label: 'Communications',
    icon: 'fas fa-envelope',
    path: '/communications',
    permissions: [PERMISSIONS.COMMUNICATIONS_VIEW],
    description: 'Email and outreach'
  },
  {
    id: 'analytics',
    label: 'Analytics',
    icon: 'fas fa-chart-bar',
    path: '/analytics',
    permissions: [PERMISSIONS.ANALYTICS_VIEW],
    description: 'Performance insights'
  },
  {
    id: 'segments',
    label: 'Segments',
    icon: 'fas fa-layer-group',
    path: '/segments',
    permissions: [PERMISSIONS.DONORS_VIEW],
    description: 'Organize donor groups'
  },
  {
    id: 'import',
    label: 'Import Data',
    icon: 'fas fa-upload',
    path: '/import',
    permissions: [PERMISSIONS.DATA_IMPORT],
    description: 'Import from files'
  }
];

export function Sidebar() {
  const [location] = useLocation();
  const [isCollapsed, setIsCollapsed] = useState(() => {
    return localStorage.getItem('sidebar-collapsed') === 'true';
  });

  const auth = useAuth();

  const toggleCollapsed = () => {
    const newCollapsed = !isCollapsed;
    setIsCollapsed(newCollapsed);
    localStorage.setItem('sidebar-collapsed', newCollapsed.toString());
  };

  // Filter navigation items based on permissions
  const visibleNavigationItems = NAVIGATION_ITEMS.filter(item => 
    !item.permissions || auth.hasAnyPermission(item.permissions)
  );

  // Don't render on mobile (mobile drawer handles mobile navigation)
  if (!isDesktopDevice()) return null;

  return (
    <aside 
      id="navigation"
      className={cn(
        "bg-school-blue-500 border-r border-school-blue-400 h-screen fixed left-0 top-0 z-30 transition-all duration-300",
        "lg:relative lg:translate-x-0",
        isCollapsed ? "w-20" : "w-80"
      )}
      data-testid="sidebar"
      role="navigation"
      aria-label="Main navigation"
    >
      <div className="flex flex-col h-full">
        {/* Header - Enhanced Logo and Branding */}
        <div className="p-6 border-b border-school-blue-400/20">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4 transition-all duration-300">
              {/* Significantly Larger Logo for Better Branding */}
              <div className={cn(
                "flex items-center justify-center bg-white rounded-xl shadow-school-lg border-2 border-school-gold-200 transition-all duration-300",
                isCollapsed ? "w-12 h-12" : "w-20 h-20"
              )}>
                <img 
                  src={logoUrl} 
                  alt="School in the Square Logo" 
                  className="w-full h-full object-contain p-1"
                />
              </div>
              {!isCollapsed && (
                <div className="flex flex-col">
                  <h3 className="text-school-heading font-bold text-white leading-tight">School in the Square</h3>
                  <p className="text-school-body text-school-blue-200 font-medium">Fundraising Platform</p>
                </div>
              )}
            </div>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={toggleCollapsed}
                  className="text-white hover:bg-school-blue-400/30 hidden lg:flex rounded-lg"
                  data-testid="button-toggle-collapse"
                  aria-label={isCollapsed ? "Expand sidebar" : "Collapse sidebar"}
                >
                  <i className={`fas ${isCollapsed ? 'fa-chevron-right' : 'fa-chevron-left'}`} />
                </Button>
              </TooltipTrigger>
              <TooltipContent side="right">
                {isCollapsed ? "Expand sidebar" : "Collapse sidebar"}
              </TooltipContent>
            </Tooltip>
          </div>
        </div>

        {/* Navigation */}
        <ScrollArea className="flex-1">
          <nav className="p-4 space-y-2" role="navigation">
            {visibleNavigationItems.map((item) => {
              const isActive = item.path === '/' ? location === '/' : location.startsWith(item.path);

              return (
                <Tooltip key={item.id}>
                  <TooltipTrigger asChild>
                    <Link
                      href={item.path}
                      className={cn(
                        "flex items-center gap-3 px-4 py-3 rounded-lg transition-all duration-200 font-medium",
                        "min-h-[48px] focus:outline-none focus:ring-2 focus:ring-school-gold-500 focus:ring-offset-2 focus:ring-offset-school-blue-500",
                        isActive
                          ? "bg-school-gold-500 text-school-blue-900 shadow-school-gold"
                          : "text-school-blue-100 hover:text-white hover:bg-school-blue-400/30"
                      )}
                      data-testid={`nav-${item.id}`}
                      aria-label={`${item.label}${isActive ? ' (current page)' : ''}`}
                    >
                      <div className="relative">
                        <i className={`${item.icon} w-5 h-5`} />
                      </div>
                      {!isCollapsed && <span className="text-school-body font-medium">{item.label}</span>}
                    </Link>
                  </TooltipTrigger>
                  {(isCollapsed || item.description) && (
                    <TooltipContent side="right" className="max-w-xs">
                      {isCollapsed ? item.label : item.description}
                    </TooltipContent>
                  )}
                </Tooltip>
              );
            })}
          </nav>
        </ScrollArea>

        {/* Footer */}
        <div className="p-4 border-t border-school-blue-400/20 space-y-3">
          {/* User & Sign Out */}
          {isCollapsed ? (
            <div className="flex flex-col items-center gap-2">
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button 
                    variant="ghost" 
                    size="sm"
                    onClick={auth.logout}
                    data-testid="button-logout"
                    className="text-school-blue-200 hover:text-white hover:bg-school-blue-400/30 rounded-lg"
                    aria-label="Sign out"
                  >
                    <i className="fas fa-sign-out-alt" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent side="right">
                  Sign Out
                </TooltipContent>
              </Tooltip>
            </div>
          ) : (
            <div className="space-y-2">
              <div className="flex items-center gap-3">
                <div className="w-8 h-8 bg-school-blue-400/30 rounded-lg flex items-center justify-center">
                  <span className="text-school-small font-semibold text-white">
                    {auth.getUserDisplayName().split(' ').map(n => n[0]).join('').toUpperCase() || 'U'}
                  </span>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-school-body font-semibold text-white truncate">
                    {auth.getUserDisplayName() || 'User'}
                  </p>
                  <p className="text-school-small text-school-blue-300 truncate">
                    {auth.user?.jobTitle || auth.getRoleDisplayName()}
                  </p>
                </div>
              </div>

              <Button
                variant="ghost"
                onClick={auth.logout}
                className="w-full justify-start text-school-blue-200 hover:text-white hover:bg-school-blue-400/30"
                data-testid="button-logout"
              >
                <i className="fas fa-sign-out-alt mr-3" />
                Sign Out
              </Button>
            </div>
          )}
        </div>
      </div>
    </aside>
  );
}

