    transition: transform 100ms var(--nav-easing-accelerate);
  }

  /* Loading shimmer effect */
  .nav-shimmer {
    background: linear-gradient(
      90deg,
      rgba(255, 255, 255, 0) 0%,
      rgba(255, 255, 255, 0.2) 20%,
