import { Permission } from '@shared/permissions';

// User maturity levels based on data and activity
export type UserMaturityLevel = 'beginner' | 'active' | 'power_user';

// Navigation item visibility rules
export type NavigationVisibility = 'always' | 'progressive' | 'admin' | 'mobile_only' | 'desktop_only';

// Navigation item interface
export interface NavigationItem {
  id: string;
  label: string;
  icon: string; // FontAwesome class name
  path: string;
  visibility: NavigationVisibility;
  permissions?: Permission[];
  requiredMaturity?: UserMaturityLevel;
  requiredData?: {
    donors?: number;
    campaigns?: number;
    communications?: number;
  };
  badge?: string | number;
  children?: NavigationItem[];
  isNew?: boolean;
  description?: string;
}

// User activity data interface
export interface UserActivityData {
  donorCount: number;
  campaignCount: number;
  communicationCount: number;
  lastLoginDays: number;
  accountAgeDays: number;
  featuresUsed: string[];
}

// User maturity detection
export function getUserMaturityLevel(activityData: UserActivityData): UserMaturityLevel {
  const { donorCount, campaignCount, communicationCount, featuresUsed, accountAgeDays } = activityData;

  // Power user criteria
  if (
    donorCount >= 25 &&
    campaignCount >= 3 &&
    communicationCount >= 10 &&
    featuresUsed.length >= 8 &&
    accountAgeDays >= 7
  ) {
    return 'power_user';
  }

  // Active user criteria
  if (
    donorCount >= 6 &&
    (campaignCount >= 1 || communicationCount >= 3) &&
    featuresUsed.length >= 4 &&
    accountAgeDays >= 2
  ) {
    return 'active';
  }

  // Beginner
  return 'beginner';
}

// Check if user should see a navigation item
export function shouldShowNavigationItem(
  item: NavigationItem,
  userMaturity: UserMaturityLevel,
  activityData: UserActivityData,
  hasPermission: (permissions: Permission[]) => boolean,
  isSimpleMode: boolean,
  isMobile: boolean
): boolean {
  // Check device-specific visibility
  if (item.visibility === 'mobile_only' && !isMobile) return false;
  if (item.visibility === 'desktop_only' && isMobile) return false;

  // Check permissions
  if (item.permissions && !hasPermission(item.permissions)) return false;

  // Check visibility rules
  switch (item.visibility) {
    case 'always':
      return true;

    case 'admin':
      // Admin items handled by permissions check above
      return true;

    case 'progressive':
      // Check maturity requirements
      if (item.requiredMaturity) {
        const maturityOrder: UserMaturityLevel[] = ['beginner', 'active', 'power_user'];
        const requiredIndex = maturityOrder.indexOf(item.requiredMaturity);
        const currentIndex = maturityOrder.indexOf(userMaturity);
        if (currentIndex < requiredIndex) return false;
      }

      // Check data requirements
      if (item.requiredData) {
        const { donors, campaigns, communications } = item.requiredData;
        if (donors && activityData.donorCount < donors) return false;
        if (campaigns && activityData.campaignCount < campaigns) return false;
        if (communications && activityData.communicationCount < communications) return false;
      }

      // In simple mode, hide advanced features unless user has sufficient data
      if (isSimpleMode && userMaturity === 'beginner') return false;

      return true;

    default:
      return true;
  }
}

// Get navigation badge text
export function getNavigationBadge(item: NavigationItem, activityData: UserActivityData): string | number | undefined {
  if (item.badge) return item.badge;

  // Dynamic badges based on data
  switch (item.id) {
    case 'donors':
      return activityData.donorCount > 0 ? activityData.donorCount : undefined;
    case 'campaigns':
      return activityData.campaignCount > 0 ? activityData.campaignCount : undefined;
    case 'communications':
      return activityData.communicationCount > 0 ? activityData.communicationCount : undefined;
    default:
      return undefined;
  }
}

// Get contextual help text for navigation items
export function getNavigationHelp(item: NavigationItem, userMaturity: UserMaturityLevel): string | undefined {
  if (item.description) return item.description;

  // Contextual help based on maturity
  const helpText = {
    dashboard: {
      beginner: "Your fundraising overview and next steps",
      active: "Track campaign performance and donor engagement",
      power_user: "Advanced analytics and strategic insights"
    },
    donors: {
      beginner: "Manage your supporter contacts and information",
      active: "Track donor relationships and giving history",
      power_user: "Advanced donor analytics and segmentation"
    },
    campaigns: {
      beginner: "Create and manage your fundraising initiatives",
      active: "Track campaign progress and optimize performance",
      power_user: "Advanced campaign analytics and A/B testing"
    },
    communications: {
      beginner: "Send emails and updates to your supporters",
      active: "Manage email campaigns and track engagement",
      power_user: "Advanced communication automation and analytics"
    }
  };

  return helpText[item.id as keyof typeof helpText]?.[userMaturity];
}

// Feature discovery helpers
export interface FeatureDiscovery {
  id: string;
  title: string;
  description: string;
  icon: string;
  path: string;
  unlockCriteria: string;
  isUnlocked: boolean;
  isNew: boolean;
}

export function getFeatureDiscoveries(
  userMaturity: UserMaturityLevel,
  activityData: UserActivityData
): FeatureDiscovery[] {
  const discoveries: FeatureDiscovery[] = [];

  // Analytics unlock
  if (userMaturity === 'beginner' && activityData.donorCount >= 5) {
    discoveries.push({
      id: 'analytics',
      title: 'Analytics Dashboard',
      description: 'Track your fundraising performance with detailed analytics',
      icon: 'fas fa-chart-bar',
      path: '/analytics',
      unlockCriteria: 'Added 5+ donors',
      isUnlocked: activityData.donorCount >= 10,
      isNew: activityData.donorCount >= 5 && activityData.donorCount < 15
    });
  }

  // Segments unlock
  if (userMaturity !== 'power_user' && activityData.donorCount >= 15) {
    discoveries.push({
      id: 'segments',
      title: 'Donor Segments',
      description: 'Create targeted groups for personalized communications',
      icon: 'fas fa-layer-group',
      path: '/segments',
      unlockCriteria: 'Added 15+ donors',
      isUnlocked: activityData.donorCount >= 25,
      isNew: activityData.donorCount >= 15 && activityData.donorCount < 30
    });
  }

  // Advanced features unlock
  if (userMaturity === 'active' && activityData.campaignCount >= 2) {
    discoveries.push({
      id: 'advanced_tools',
      title: 'Advanced Tools',
      description: 'Access bulk operations, automation, and advanced reporting',
      icon: 'fas fa-cogs',
      path: '/settings',
      unlockCriteria: 'Created 2+ campaigns',
      isUnlocked: userMaturity === 'power_user',
      isNew: false
    });
  }

  return discoveries;
}

// Device detection utilities
export function isMobileDevice(): boolean {
  if (typeof window === 'undefined') return false;
  return window.innerWidth < 768;
}

export function isTabletDevice(): boolean {
  if (typeof window === 'undefined') return false;
  return window.innerWidth >= 768 && window.innerWidth < 1024;
}

export function isDesktopDevice(): boolean {
  if (typeof window === 'undefined') return false;
  return window.innerWidth >= 1024;
}

// Touch device detection
export function isTouchDevice(): boolean {
  if (typeof window === 'undefined') return false;
  return 'ontouchstart' in window || navigator.maxTouchPoints > 0;
}

// Keyboard shortcuts helper
export const KEYBOARD_SHORTCUTS = {
  SEARCH: { key: 'k', meta: true, description: 'Global search' },
  DASHBOARD: { key: '1', meta: true, description: 'Go to Dashboard' },
  DONORS: { key: '2', meta: true, description: 'Go to Donors' },
  CAMPAIGNS: { key: '3', meta: true, description: 'Go to Campaigns' },
  COMMUNICATIONS: { key: '4', meta: true, description: 'Go to Communications' },
  ADD_DONOR: { key: 'n', meta: true, description: 'Add new donor' },
  TOGGLE_SIDEBAR: { key: 'b', meta: true, description: 'Toggle sidebar' }
} as const;

// Accessibility helpers
export function getAriaLabel(item: NavigationItem, isActive: boolean): string {
  const activeText = isActive ? ', current page' : '';
  const badgeText = item.badge ? `, ${item.badge} items` : '';
  return `${item.label}${badgeText}${activeText}`;
}

export function getAriaDescribedBy(item: NavigationItem): string | undefined {
  return item.description ? `nav-help-${item.id}` : undefined;
}

client/src/stores/navigation-store.ts.content:
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface NavigationState {
  // Basic navigation state
  isMobileNavOpen: boolean;
  isDesktopSidebarCollapsed: boolean;

  // Actions
  setMobileNavOpen: (open: boolean) => void;
  toggleDesktopSidebar: () => void;
}

export const useNavigationStore = create<NavigationState>()(
  persist(
    (set) => ({
      // Initial state
      isMobileNavOpen: false,
      isDesktopSidebarCollapsed: false,

      // Actions
      setMobileNavOpen: (open) => set({ isMobileNavOpen: open }),

      toggleDesktopSidebar: () => set((state) => ({
        isDesktopSidebarCollapsed: !state.isDesktopSidebarCollapsed
      }))
    }),
    {
      name: 'navigation-store',
      // Only persist user preferences
      partialize: (state) => ({
        isDesktopSidebarCollapsed: state.isDesktopSidebarCollapsed
      }),
    }
  )
);

// Hook for mobile navigation state
export function useMobileNavigation() {
  const { 
    isMobileNavOpen, 
    setMobileNavOpen 
  } = useNavigationStore();

  return {
    isOpen: isMobileNavOpen,
    open: () => setMobileNavOpen(true),
    close: () => setMobileNavOpen(false),
    toggle: () => setMobileNavOpen(!isMobileNavOpen)
  };
}

server/security-middleware.ts.content:
import helmet from 'helmet';
import cors from 'cors';
import rateLimit, { ipKeyGenerator } from 'express-rate-limit';
import slowDown from 'express-slow-down';
import compression from 'compression';
import { Request, Response, NextFunction, Express } from 'express';
import { z } from 'zod';
import { triggerManualAlert, AlertType, AlertSeverity } from './production-alerts';

// Security configuration based on environment
const isProduction = process.env.NODE_ENV === 'production';
const isDevelopment = !isProduction;

// CORS configuration
export const corsOptions = {
  origin: function (origin: string | undefined, callback: (err: Error | null, allow?: boolean) => void) {
    // Allow requests with no origin (like mobile apps or curl requests)
    if (!origin) return callback(null, true);

    const allowedOrigins = process.env.ALLOWED_ORIGINS?.split(',') || [];

    // In development, allow localhost and Replit domains
    if (isDevelopment) {
      const devOrigins = [
        'http://localhost:5000',
        'http://127.0.0.1:5000',
        /\.repl\.co$/,
        /\.replit\.dev$/,
        /\.replit\.app$/
      ];

      const isDevOriginAllowed = devOrigins.some(pattern => {
        if (typeof pattern === 'string') {
          return origin === pattern;
        }
        return pattern.test(origin);
      });

      if (isDevOriginAllowed) {
        return callback(null, true);
      }
    }

    // Check production allowed origins
    if (allowedOrigins.includes(origin)) {
      callback(null, true);
    } else {
      console.warn(`CORS: Blocked request from origin: ${origin}`);
      callback(new Error('Not allowed by CORS'), false);
    }
  },
  credentials: true,
  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS', 'PATCH'],
  allowedHeaders: [
    'Origin', 
    'X-Requested-With', 
    'Content-Type', 
    'Accept', 
    'Authorization',
    'X-Api-Key',
    'X-Csrf-Token'
  ],
  exposedHeaders: ['X-Total-Count', 'X-Page-Count'],
  maxAge: 86400 // 24 hours
};

// Security headers configuration
export const helmetOptions = {
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:"],
      scriptSrc: ["'self'", ...(isDevelopment ? ["'unsafe-eval'", "'unsafe-inline'"] : [])],
      connectSrc: ["'self'", "https://api.openai.com"],
      frameSrc: ["'none'"],
      objectSrc: ["'none'"],
      upgradeInsecureRequests: isProduction ? [] : null,
    },
  },
  crossOriginEmbedderPolicy: false, // Allows images from external sources
  hsts: {
    maxAge: 31536000,
    includeSubDomains: true,
    preload: true
  }
};

// Rate limiting configurations
export const generalRateLimit = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: isDevelopment ? 1000 : 100, // More lenient in development
  message: {
    error: 'Too many requests',
    code: 'RATE_LIMIT_EXCEEDED',
    retryAfter: '15 minutes'
  },
  standardHeaders: true,
  legacyHeaders: false,
  keyGenerator: (req: Request) => {
    // Use user ID if authenticated, otherwise IP with IPv6 support
    return (req as any).userId || ipKeyGenerator(req) || 'anonymous';
  },
  skip: (req: Request) => {
    // Skip rate limiting for health checks and static assets
    return req.path === '/health' || req.path.startsWith('/assets/');
  }
});

// Stricter rate limiting for authentication endpoints
export const authRateLimit = rateLimit({
  windowMs: 5 * 60 * 1000, // 5 minutes
  max: isDevelopment ? 50 : 5, // Very strict for auth endpoints
  message: {
    error: 'Too many authentication attempts',
    code: 'AUTH_RATE_LIMIT_EXCEEDED',
    retryAfter: '5 minutes'
  },
  standardHeaders: true,
  legacyHeaders: false,
  skipSuccessfulRequests: true, // Don't count successful requests
});

// API endpoint rate limiting
export const apiRateLimit = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: isDevelopment ? 500 : 200,
  message: {
    error: 'API rate limit exceeded',
    code: 'API_RATE_LIMIT_EXCEEDED',
    retryAfter: '15 minutes'
  },
  standardHeaders: true,
  legacyHeaders: false,
  keyGenerator: (req: Request) => {
    return (req as any).userId || ipKeyGenerator(req) || 'anonymous';
  }
});

// Slow down for expensive operations
export const expensiveOperationSlowDown = slowDown({
  windowMs: 15 * 60 * 1000, // 15 minutes
  delayAfter: isDevelopment ? 10 : 5,
  delayMs: () => 500,
  maxDelayMs: 10000,
  skipSuccessfulRequests: true,
});

// Request size limits
export const requestSizeLimits = {
  json: { limit: '10mb' },
  urlencoded: { limit: '10mb', extended: true },
  raw: { limit: '50mb' }, // For file uploads
};

// Input sanitization middleware
export function sanitizeInput(req: Request, res: Response, next: NextFunction) {
  try {
    // Recursively sanitize object properties
    const sanitizeObject = (obj: any): any => {
      if (typeof obj === 'string') {
        // Remove potentially dangerous characters
        return obj
          .replace(/[<>]/g, '') // Remove HTML brackets
          .replace(/javascript:/gi, '') // Remove javascript: protocol
          .replace(/on\w+=/gi, '') // Remove event handlers
          .trim();
      }

      if (Array.isArray(obj)) {
        return obj.map(sanitizeObject);
      }

      if (obj && typeof obj === 'object') {
        const sanitized: any = {};
        for (const [key, value] of Object.entries(obj)) {
          // Skip potentially dangerous keys
          if (!key.match(/^[a-zA-Z0-9_-]+$/)) {
            continue;
          }
          sanitized[key] = sanitizeObject(value);
        }
        return sanitized;
      }

      return obj;
    };

    // Sanitize request body
    if (req.body) {
      req.body = sanitizeObject(req.body);
    }

    // Sanitize query parameters
    if (req.query) {
      req.query = sanitizeObject(req.query);
    }

    // Sanitize URL parameters
    if (req.params) {
      req.params = sanitizeObject(req.params);
    }

    next();
  } catch (error) {
    console.error('Input sanitization error:', error);
    res.status(400).json({
      error: 'Invalid input data',
      code: 'INVALID_INPUT'
    });
  }
}

// Request validation middleware factory
export function validateRequest<T>(schema: z.ZodSchema<T>, source: 'body' | 'query' | 'params' = 'body') {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const data = req[source];
      const validatedData = schema.parse(data);
      req[source] = validatedData;
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: 'Validation failed',
          code: 'VALIDATION_ERROR',
          details: error.errors.map(err => ({
            field: err.path.join('.'),
            message: err.message,
            code: err.code
          }))
        });
      }

      console.error('Validation middleware error:', error);
      res.status(500).json({
        error: 'Internal validation error',
        code: 'VALIDATION_INTERNAL_ERROR'
      });
    }
  };
}

// Security audit middleware - logs security-relevant events
export function securityAudit(req: Request, res: Response, next: NextFunction) {
  const securityContext = {
    ip: req.ip || req.connection.remoteAddress,
    userAgent: req.get('User-Agent'),
    referer: req.get('Referer'),
    method: req.method,
    path: req.path,
    userId: (req as any).userId,
    timestamp: new Date().toISOString(),
  };

  // Log potentially suspicious activities
  const suspiciousPatterns = [
    /\.\./,  // Path traversal
    /<script/i,  // XSS attempts
    /union.*select/i,  // SQL injection
    /javascript:/i,  // JavaScript protocol
    /eval\(/i,  // Code injection
  ];

  const fullUrl = req.originalUrl || req.url;
  const isSuspicious = suspiciousPatterns.some(pattern => 
    pattern.test(fullUrl) || 
    pattern.test(JSON.stringify(req.body || {})) ||
    pattern.test(JSON.stringify(req.query || {}))
  );

  if (isSuspicious) {
    console.warn('ðŸš¨ Suspicious request detected:', {
      ...securityContext,
      body: req.body,
      query: req.query,
      url: fullUrl
    });
  }

  // Add security headers to response
  res.set({
    'X-Request-ID': Math.random().toString(36).substring(2),
    'X-Content-Type-Options': 'nosniff',
    'X-Frame-Options': 'DENY',
    'X-XSS-Protection': '1; mode=block',
    'Referrer-Policy': 'strict-origin-when-cross-origin'
  });

  next();
}

// Error handling middleware for security
export function securityErrorHandler(err: any, req: Request, res: Response, next: NextFunction) {
  // Don't leak internal error details in production
  const isDevelopmentMode = isDevelopment;

  // Log the full error for debugging
  console.error('Security Error:', {
    error: err.message,
    stack: isDevelopmentMode ? err.stack : undefined,
    path: req.path,
    method: req.method,
    ip: req.ip,
    userAgent: req.get('User-Agent'),
    userId: (req as any).userId
  });

  // Determine appropriate error response
  let statusCode = err.statusCode || err.status || 500;
  let message = 'Internal server error';
  let code = 'INTERNAL_ERROR';

  if (statusCode === 400) {
    message = 'Bad request';
    code = 'BAD_REQUEST';
  } else if (statusCode === 401) {
    message = 'Unauthorized';
    code = 'UNAUTHORIZED';
  } else if (statusCode === 403) {
    message = 'Forbidden';
    code = 'FORBIDDEN';
  } else if (statusCode === 404) {
    message = 'Not found';
    code = 'NOT_FOUND';
  } else if (statusCode === 429) {
    message = 'Too many requests';
    code = 'RATE_LIMITED';
  }

  const errorResponse: any = {
    error: message,
    code,
    timestamp: new Date().toISOString()
  };

  // Include more details in development
  if (isDevelopmentMode) {
    errorResponse.details = err.message;
    if (err.errors) {
      errorResponse.validationErrors = err.errors;
    }
  }

  res.status(statusCode).json(errorResponse);
}

// Compression middleware configuration
export const compressionOptions = {
  filter: (req: Request, res: Response) => {
    // Don't compress responses if this request has a 'x-no-compression' header
    if (req.headers['x-no-compression']) {
      return false;
    }

    // fallback to standard filter function
    return compression.filter(req, res);
  },
  threshold: 1024, // Only compress responses larger than 1KB
  level: 6, // Compression level (1-9)
};

// Health check endpoint - bypasses most security measures
export function healthCheck(req: Request, res: Response) {
  res.status(200).json({
    status: 'healthy',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    memory: process.memoryUsage(),
    environment: process.env.NODE_ENV || 'development'
  });
}

// Setup all security middleware
export function setupSecurityMiddleware(app: Express) {
  // Trust proxy (for rate limiting and IP detection)
  app.set('trust proxy', 1);

  // Compression (should be early in the chain)
  app.use(compression(compressionOptions));

  // Security headers
  app.use(helmet(helmetOptions));

  // CORS configuration
  app.use(cors(corsOptions));

  // Health check endpoint (before rate limiting)
  app.get('/health', healthCheck);

  // General rate limiting
  app.use(generalRateLimit);

  // Security audit logging
  app.use(securityAudit);

  // Input sanitization (before body parsing)
  app.use(sanitizeInput);

  console.log('ðŸ”’ Security middleware configured successfully');
}

// Apply specific rate limits to certain routes
export function applyRouteSpecificSecurity(app: Express) {
  // Authentication endpoints
  app.use('/api/login', authRateLimit);
  app.use('/api/callback', authRateLimit);
  app.use('/api/logout', authRateLimit);

  // API endpoints
  app.use('/api/', apiRateLimit);

  // Expensive operations (imports, AI)
  app.use('/api/import', expensiveOperationSlowDown);
  app.use('/api/ai', expensiveOperationSlowDown);

  console.log('ðŸš¨ Route-specific security configured');
}

